function BiSvg() {
    return (
        <svg
            xmlns="http://www.w3.org/2000/svg"
            height={80}
            style={{ display: 'inline', marginLeft: '10px' }}
            width={40}
            viewBox="0 0 800 480">
            <g fillRule="evenodd">
                <path fill="#d60270" d="M0 0h800v192H0z" />
                <path fill="#9b4f96" d="M0 192h800v96H0z" />
                <path fill="#0038a8" d="M0 288h800v192H0z" />
            </g>
            <script />
        </svg>
    );
}

